const form=document.getElementById("form"),email=document.getElementById("form_input"),error=document.getElementById("form_error");function showError(e){email.style.borderColor="hsl(0, 94%, 66%)",error.innerHTML=e}function showSucces(e){email.style.borderColor="transparent",error.innerHTML=e}function checkRequired(e){""===e.value.trim()?showError("Cannot be empty"):showSucces("")}function checkEmail(e){/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(e.value.trim())?showSucces(e,""):showError("Whoops, make sure it's an email")}form.addEventListener("submit",function(e){e.preventDefault(),checkRequired(email),checkEmail(email)});const links=document.getElementsByClassName("questions_list_item"),answers=document.getElementsByClassName("answer"),imgOpen=document.getElementsByClassName("img_question"),imgClose=document.getElementsByClassName("img_answer");let open=!1;for(let e=0;e<links.length;e++)links[e].addEventListener("click",function(){open?(answers[e].classList.remove("active"),imgOpen[e].style.display="block",imgClose[e].style.display="none",open=!1):(answers[e].classList.add("active"),imgOpen[e].style.display="none",imgClose[e].style.display="block",open=!0)});